package com.phlink.example.server.service.push;import com.phlink.grpc.examples.proto.Status;import com.phlink.grpc.examples.proto.TopicInfo;import java.util.HashSet;import java.util.Set;import java.util.concurrent.ConcurrentHashMap;public class DefaultTopicService implements ITopicService {	private ConcurrentHashMap<String, Set<String>> topics = new ConcurrentHashMap<>();		public Status subscribeTopic(TopicInfo topicInfo) {		String id = topicInfo.getClientInfo().getClientId();		String topic = topicInfo.getTopicName();		Set<String> ids = topics.computeIfAbsent(topic, k -> new HashSet<>());		ids.add(id);		return Status.newBuilder().setStatus("OK").build();	}	public Status cancelTopic(TopicInfo topicInfo) {		String topic = topicInfo.getTopicName();		Set<String> ids  = topics.get(topic);		if(ids==null){			return Status.newBuilder().setStatus("OK").build();		}		ids.remove(topicInfo.getClientInfo().getClientId());		return Status.newBuilder().setStatus("OK").build();	}	public Set<String> getClientId(String topic) {		return topics.get(topic);	}}